mapper.select:
select id, name
from table(pkg_zp_card_api.get_regions)
order by sort_order

select id, name, id as parentid
from table(pkg_zp_card_api.get_regions)
order by sort_order

select id, name, parentid
from table(pkg_zp_card_api.get_cities)
order by id

select id, name
from table(pkg_zp_card_api.get_branch_types)
order by sort_order

select DO_CODE Id, DO_Name Name, DO_Name branchName, City_Id CityId, Branch_Type_Id BranchTypeId
from table(pkg_zp_card_api.get_DOs)
order by sort_order

select id, name
from table(pkg_zp_card_api.get_card_categories)
order by sort_order

select Id, Name
from table(pkg_zp_card_api.get_client_segments)
order by sort_order

select Id, Name
from table(pkg_zp_card_api.get_dimensions)
order by sort_order

select Id, Name
from table(pkg_zp_card_api.get_mvk_type)
order by sort_order

select Id, Name
from table(pkg_zp_card_api.get_reissue_speed)
order by sort_order

select id, name
from table(pkg_zp_card_api.get_zp_type)
order by sort_order


select Code Id, Name, ParentId
from table(pkg_zp_card_api.get_srvpacks)
where Code < '200'

select Id, Name
from table(pkg_zp_card_api.get_timeunits)
order by sort_order
