mapper.select:
select id, name, parent_id as parentid, ccode as code
from table(pkg_nom_api.get_InnerEndProducts(p_levels => '1,2'))
order by sort_order

select id, name
from table(pkg_nom_api.get_ProfitCenters)
order by sort_order

select id, name
from table(pkg_nom_api.get_Blocks)
order by sort_order
